import React from 'react';
import axios from "axios"
import Router from 'next/router'
import StripeCheckout from 'react-stripe-checkout';
import { connect } from 'react-redux';
import { toast } from 'react-toastify';
import { resetCart } from '../../store/actions/actions';

import CoinbaseCommerceButton from 'react-coinbase-commerce';

class Payments extends React.Component {
    handleClick = () => {
        this.props.resetCart(); 
        toast.success('Order has been confirmed', {
            position: "top-center",
            autoClose: 3000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true
        });

        setTimeout(function(){ Router.push('/thankyou'); }, 3000);
    }
    render(){
        let { amount } = this.props;
        const checkoutId = async checkout => {
            const body = {
                amount: amount,
                token: token
            };  
            await axios.post("/api/checkout", body);
        };


        return (
            <React.Fragment>
                <div className="order-btn">

                    <CoinbaseCommerceButton
              wrapperStyle={{ width: '100%' }}
              style={{
                width: '100%',
                color: 'green',
                borderColor: 'green',
                borderRadius: 4,
                height: 45,
                cursor: 'pointer',
              }}
              checkoutId={'a3dee041-b038-46eb-9785-2a459d5554a6'}>
              Custom Styles Button
              </CoinbaseCommerceButton>
                </div>
            </React.Fragment>
        );
    }
}

const mapDispatchToProps= (dispatch)=>{
    return {
        resetCart: () => { dispatch(resetCart()) }
    }
}

export default connect(
    null,
    mapDispatchToProps
)(Payments)
